<?php		include $_SERVER['DOCUMENT_ROOT']."/scripts/sql/sqlCore.php";	include $_SERVER['DOCUMENT_ROOT']."/scripts/conversion/js_php_array_unpacker.php";		// Get Results:	if(isset($_POST["input"])) {		$input = unpackJSArray($_POST["input"], '|');		$inputValues = Array();		foreach($input as $inputKey => $inputValue)			$inputValues[$inputKey] = $inputValue["value"];		$keyValues = sqlKeyValues($inputValues);		sqlRun("INSERT INTO meals_pending (".$keyValues["keys"].") VALUES(".$keyValues["values"].")");				// Generate and Send Email:		$emailTo = $inputValues["email"];		$emailFrom = "thebears@grizzlychow.com";		$emailSubject = "Thanks For The Contribution!";		$emailMessage = "Thanks! The bears will now review the meal you added!\n\n";		$emailMessage .= "========== Your Meal ==========\n";		$emailMessage .= "Name: ".$inputValues["name"]."\n";		$emailMessage .= "Description: ".$inputValues["description"]."\n";		$emailMessage .= "Ingredients: ".$inputValues["ingredients"]."\n";		$emailMessage .= "Image: ".$inputValues["image"]."\n";				$emailHeaders = 'From: '.$emailFrom."\r\n".			'Reply-To: '.$emailFrom."\r\n".			'X-Mailer: PHP/'.phpversion();		@mail($emailTo, $emailSubject, $emailMessage, $emailHeaders);	}	else {		header('Location: http://grizzlychow.pennydroid.com');	}	
?>