<?php		include $_SERVER['DOCUMENT_ROOT']."/scripts/sql/sqlCore.php";	include $_SERVER['DOCUMENT_ROOT']."/scripts/conversion/js_php_array_unpacker.php";		// Check Login:	$loginValid = false;	if(isset($_POST["login"])) {		$login = unpackJSArray($_POST["login"], '|');		$userID = sqlGetFirst("id", "users", "username|password", "'".$login["username"]."'"."|"."'".$login["password"]."'");				// Valid Login@		if($userID >= 1)			$loginValid = true;				// Bad Login:		if(!$loginValid) {			header('Location: http://grizzlychow.com/login.php');		}	}	else // No Login:		header('Location: http://grizzlychow.com/login.php');	
