<?php		include $_SERVER['DOCUMENT_ROOT']."/scripts/sql/sqlCore.php";	include $_SERVER['DOCUMENT_ROOT']."/scripts/conversion/js_php_array_unpacker.php";		// Get Search Type:	if(isset($_POST["search_type"]))		$searchType = $_POST["search_type"];		if(isset($_GET["id"]))		$searchType = "ID";			// Get Results:	if(isset($searchType)) {		if($searchType == "Random") {			$entryMax = sqlProcessFirst(sqlRun("SELECT MAX(id) FROM meals"));			$entryMax = $entryMax["MAX(id)"] - 1;			$entryRandom = rand(0, $entryMax);			$meal = sqlProcessFirst(sqlRun("SELECT * FROM meals WHERE id >= ".$entryRandom." ORDER BY id ASC LIMIT 1"));		}		else if($searchType == "ID") {			$meal = sqlProcessFirst(sqlRun("SELECT * FROM meals WHERE id = ".$_GET["id"]." ORDER BY id ASC LIMIT 1"));		}	}	
?>